import React, {useEffect, useState} from 'react';
import Button from './Button'
import RedditData from './RedditData';

const Header = ({title}) => {
    const [subRedditName, setSubRedditName] = useState('');
    const [showRedditData, setShowRedditData] = useState(false);

    const click = () =>{
        setShowRedditData(true)
        if(subRedditName){
            setSubRedditName(subRedditName)
        }
    }

    const change = event =>{
        setSubRedditName(event.target.value)
    }
    return(
        <div className='header'>
            <h1 className='Title-Header'>{title}</h1>
            <input onChange={change}
            type="text" 
            className='Search-Box' 
            placeholder='Enter a subreddit'
            value={subRedditName}/>

            <Button text={"Retrieve top 10 posts"} colour={"red"} onClick={click}/>
            {showRedditData && subRedditName && <RedditData subRedditName={subRedditName} />}
        </div>
    )
}

Header.defaultProps={
    title: 'Reddit Hotness Grabber'
}
export default Header;
