import React, {useEffect, useState} from 'react';
import Button from './Button'
import RedditData from './RedditData';

const Header = ({title}) => {
    const [subRedditName, setSubRedditName] = useState('');

    return(
        <div className='header'>
            <h1 className='Title-Header'>{title}</h1>
            <input 
            type="text" 
            className='Search-Box' 
            placeholder='Enter a subreddit'
            value={subRedditName}
            onChange={(event) => setSubRedditName(event.target.value)}
            />
            <Button text={"Retrieve top 10 posts"} colour={"red"} />
            {subRedditName && <RedditData subRedditName={subRedditName}/>}
        </div>
    )
}

const handleButtonClick = () => {
    alert('Peni')
}

Header.defaultProps={
    title: 'Reddit Hotness Grabber'
}
export default Header;
