import React, {useEffect, useState} from 'react';
import Button from './Button'
import RedditData from './RedditData';

const Header = ({title}) => {
    const [subRedditName, setSubRedditName] = useState('');
    const [likedRedditPosts, setLikedRedditPsots] = useState([{}]);

    const [inputName, setInputName] = useState('');

    const click = () =>{
        setSubRedditName(inputName)
    }

    const change = event => {
        setInputName(event.target.value)
    }

    return(
        <div className='header'>
            <div className='Header-Content'>
                <h1 className='Title-Header'>{title}</h1>           
                <input 
                onChange={change}
                type="text" 
                className='Search-Box' 
                placeholder='Enter a subreddit'
                value={inputName}
                />
                <Button text={"Retrieve top 10 posts"} colour={"red"} onClick={click}/>
            </div>
            <div className='Web-Content'>
                {subRedditName && <RedditData subRedditName={subRedditName} />}
            </div>
        </div>
    )
}

Header.defaultProps={
    title: 'Reddit Hotness Grabber'
}
export default Header;
